def ground_truth_process_graph(graph_data):
    # Create a new graph
    new_graph = nx.Graph()

    # Get the unique labels from the graph
    labels = set()
    for node in graph_data.nodes():
        labels.update(graph_data.nodes[node]['labels'])

    # Create a node for each unique label
    for label in labels:
        new_graph.add_node(label)

    # Connect the nodes with those labels in the new graph if they are not already connected by an edge
    for edge in graph_data.edges():
        source_labels = graph_data.nodes[edge[0]]['labels']
        target_labels = graph_data.nodes[edge[1]]['labels']
        for source_label in source_labels:
            for target_label in target_labels:
                if not new_graph.has_edge(source_label, target_label):
                    new_graph.add_edge(source_label, target_label)

    # Return the networkx graph object
    return_object = {
        'type': 'graph',
        'data': new_graph
    }
    return return_object

